export default function useDebouncedPromise(fn, delay) {

  function handler(...params) {
    return new Promise((resolve, reject) => {
      window.setTimeout(async () => {
        try {
          const response = await fn(...params);
          resolve(response)
        } catch (e) {
          reject(e);
        }
      }, delay);
    });
  }
  return handler;
}
